(function() {
  var Grid;

  Grid = function(_arg) {
    var canvas, canvasHeight, canvasWidth, color, context, guide, height, width, _i, _ref, _results;
    _ref = _arg != null ? _arg : {}, width = _ref.width, height = _ref.height, guide = _ref.guide, color = _ref.color;
    if (color == null) {
      color = "rgba(0, 0, 0, 0.3)";
    }
    if (height == null) {
      height = 32;
    }
    if (width == null) {
      width = 32;
    }
    if (guide == null) {
      guide = 4;
    }
    canvasWidth = width * guide;
    canvasHeight = height * guide;
    canvas = document.createElement("canvas");
    canvas.width = canvasWidth;
    canvas.height = canvasHeight;
    context = canvas.getContext("2d");
    context.fillStyle = color;
    (function() {
      _results = [];
      for (var _i = 0; 0 <= guide ? _i < guide : _i > guide; 0 <= guide ? _i++ : _i--){ _results.push(_i); }
      return _results;
    }).apply(this).forEach(function(i) {
      context.fillRect(i * width, 0, 1, canvasHeight);
      return context.fillRect(0, i * height, canvasWidth, 1);
    });
    context.fillRect(0, 0, 1, canvasHeight);
    context.fillRect(0, 0, canvasWidth, 1);
    return {
      backgroundImage: function() {
        return "url(" + (this.toDataURL()) + ")";
      },
      toDataURL: function() {
        return canvas.toDataURL("image/png");
      }
    };
  };

  module.exports = Grid;

}).call(this);

